css:
.print-format{
    margin-top:35%;
    line-height:14px;
    font-family: "Times New Roman", Times, serif;
}

html:
{% if doc.docstatus == 0 %}
<p style="text-align: right;">DRAFT</p>
{% endif %}
<div>
    <table width=100%>
        <tr>
            <td colspan=4 style="font-size:14px;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;-bottom:2px solid black;">STIPEND FOR THE MONTH OF</td>
            <td colspan=1 style="font-size:14px;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;">{{frappe.utils.format_date(doc.start_date ,'MMM-yyyy')}}</td>
            {%set esi_no= frappe.db.get_value("Employee",{"name":doc.employee},"esi_no")%}
            <td colspan=1 style="font-size:14px;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">ESI No: {{ esi or '' }}</td>
        </tr>
        <tr>
            <td colspan=4 style="font-size:14px;font-weight:bold;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">{{doc.employee_name or ''}}</td>
            <td colspan=1 style="font-size:14px;text-align:right;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">EMP No.</td>
             <td colspan=1 style="font-size:14px;text-align:right;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{doc.employee}}</td>
        </tr>
        <tr>
            <td colspan=1 rowspan=2 style="font-size:13px;text-align:center;font-weight:bold;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">PARTICULARS</td>
            <td colspan=1 rowspan=2 style="font-size:13px;text-align:center;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">FIXED</td>
            <td colspan=2 style="font-size:13px;text-align:center;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">EARNINGS</td>
            <td colspan=1 rowspan=2 style="font-size:13px;text-align:center;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">DEDUCTIONS</td>
            <td colspan=1 rowspan=2 style="font-size:13px;text-align:center;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">AMOUNT</td>
        </tr>
        <tr>
            <td colspan=1 style="font-size:13px;text-align:center;font-weight:bold;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">PAYMENT DAYS</td>
            <td colspan=1 style="font-size:13px;text-align:center;font-weight:bold;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">AMOUNT</td>
        </tr>
        <tr>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">STIPEND</td>
            {% set st = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Basic"}, "amount") or 0 %}
            {% set dst = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Dearness Allowance"}, "amount") or 0 %}
            {% set tot = (st or 0) + (dst or 0) %}
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(doc.custom_total_fixed_amount) or '' }}</td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ doc.payment_days }}</td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(tot) }}</td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">EMPLOYEE STATE INSURANCE</td>
            {% set esi = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Employee State Insurance"}, "amount") or 0 %}
            <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(esi) }}</td>
        </tr>


        <tr>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">OT Hrs</td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{doc.custom_overtime_hours or ''}}</td>
            {% set ot = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Overtime Amount"}, "amount") or 0 %}
            {% if ot is none %}
                <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            {% else %}
                <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(ot) }}</td>
            {% endif %}
            <!--<td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{esi or ''}}</td>-->
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">ADVANCE</td>
