            {% else %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(adv) }}</td>
            {% endif %}
            <!--<td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{esi or ''}}</td>-->
        </tr>
        <tr>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">OTHERS</td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            {% set oa = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Other Allowance"}, "amount") or 0 %}
            {% if oa is none %}
                <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            {% else %}
                <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(oa) }}</td>
            {% endif %}
            <!--<td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ esi or ''}}</td>-->
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">EB CHARGES</td>
