              {% if eb is none %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(eb) }}</td>
            {% endif %}
            <!--<td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{esi or ''}}</td>-->
        </tr>
        <tr>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">HEAT ALLOWANCE</td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            {% set ha = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Heat Allowance"}, "amount") %}
            {% if ha is none %}
                <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            {% else %}
                <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(ha) }}</td>
            {% endif %}
            <!--<td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{esi or ''}}</td>-->
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">MAINTENANCE CHARGE</td>
            {% set mc = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Maintance Charge"}, "amount") %}
              {% if mc is none %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(mc) }}</td>
            {% endif %}
            <!--<td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{esi or ''}}</td>-->
        </tr>
        <tr>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">SPECIAL ALLOWANCES</td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            {% set sa = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Special Allowance"}, "amount") %}
            {% if sa is none %}
                <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            {% else %}
                <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(sa) }}</td>
            {% endif %}
            <!--<td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{st or ''}}</td>-->
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">SHOE DEBIT</td>
            {% set sd = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "SHOE DEBIT"}, "amount") %}
              {% if sd is none %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(sd) }}</td>
            {% endif %}
            <!--<td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{esi or ''}}</td>-->
        </tr>
        <tr>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">T-SHIRT</td>
            {% set uni = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Uniform"}, "amount") %}
              {% if uni is none %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(uni) }}</td>
            {% endif %}
            <!--<td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{esi or ''}}</td>-->
        </tr>
        <tr>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">CANTEEN</td>
            {% set canteen = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Canteen"}, "amount") %}
            {% if canteen is none %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">0</td>
            {% else %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(canteen) }}</td>
            {% endif %}

            <!--<td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{esi or ''}}</td>-->
        </tr>
        <tr>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">CONTRIBUTION</td>
            {% set con = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Contribution"}, "amount") %}
             {% if con is none %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(con) }}</td>
            {% endif %}
            <!--<td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{esi or ''}}</td>-->
        </tr>
        <tr>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            <td colspan=1 style="font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">Late Penalty</td>
            {% set con = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Late Penalty"}, "amount") %}
             {% if con is none %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{ "{:,.2f}".format(con) }}</td>
            {% endif %}
            <!--<td colspan=1 style="text-align:right;font-size:13px;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{esi or ''}}</td>-->
        </tr>
        <tr>
            <td colspan=1 style="font-size:13px;font-weight:bold;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">TOTAL</td>
            <td colspan=1 style="font-size:13px;font-weight:bold;text-align:right;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{"{:,.2f}".format( doc.custom_total_fixed_amount) or ''}}</td>
            <td colspan=1 style="font-size:13px;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;"></td>
