            <td colspan=1 style="font-size:13px;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">TOTAL</td>
            <td colspan=1 style="font-size:13px;font-weight:bold;text-align:right;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{"{:,.2f}".format(doc.total_deduction) or ''}}</td>
        </tr>
        <tr>
            <td colspan=3 style="font-size:13px;font-weight:bold;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">SB A/C No:</td>
            <td colspan=1 style="font-size:13px;text-align:right;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{doc.bank_account_no or ''}}</td>
            <td colspan=1 style="font-size:13px;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">NET PAY</td>
            <td colspan=1 style="font-size:13px;text-align:right;font-weight:bold;border-bottom:2px solid black;border-top:2px solid black;border-left:2px solid black;border-right:2px solid black;">{{"{:,.2f}".format(doc.rounded_total) or ''}}</td>
