    </table><br><br>
    <table width=100%>
        {%if doc.leave_details %}
        {% set earned_leave = doc.leave_details | selectattr('leave_type', 'equalto', 'Earned Leave') | list | first %}
        {% if earned_leave %}
            <tr>
                <td colspan=3 style="font-size:13px;"><b>Total EL Days:</b> {{ earned_leave.total_allocated_leaves }}</td>
                <td colspan=3 style="font-size:13px;text-align:center"><b>Availed EL Days:</b> {{ earned_leave.used_leaves }}</td>
                <td colspan=3 style="font-size:13px;text-align:right"><b>Balance EL Days:</b> {{ earned_leave.available_leaves }}</td>
            </tr>
        {% else %}
            <tr>
                <td colspan=3 style="font-size:13px;"><b>Total EL Days:</b> 0</td>
                <td colspan=3 style="font-size:13px;text-align:center"><b>Availed EL Days:</b> 0</td>
                <td colspan=3 style="font-size:13px;text-align:right"><b>Balance EL Days:</b> 0</td>
            </tr>
