css:
.print-format{
    margin-top:35%;
    line-height:14px;
    margin-left:5mm;
    margin-right:5mm;
    font-family: "Times New Roman", Times, serif;
}

html:
{% if doc.docstatus == 0 %}
<p style="text-align: right;">DRAFT</p>
{% endif %}
<div>
    <table width=100%>
        <tr>
            <td colspan=4 style="font-size:16px;text-align:center;font-weight:bold;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">WONJIN AUTOPARTS INDIA PVT LTD - MM NAGAR.</td>
            <td colspan=3 style="font-size:16px;font-weight:bold;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">PAYSLIP - </td>
            <td colspan=1 style="font-size:16px;text-align:center;font-weight:bold;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">{{frappe.utils.format_date(doc.start_date ,'MMM-yy')}}</td>
        </tr>
        <tr>
            <td colspan=8 style="border-left:2px solid black;border-right:2px solid black;"></td>
        </tr>
        <tr>
            <td colspan=1 style="text-align:left;border-left:2px solid black;font-size:13px;font-weight:bold;width:4%">Code:</td>
            <td colspan=1 style='text-align:left;font-size:13px;width:12%'>{{doc.employee}}</td>
            <td colspan=1 style='text-align:left;font-size:13px;font-weight:bold'>Department:</td>
            <td colspan=1 style='text-align:left;font-size:13px;width:15%'>{{doc.department or ''}}</td>
            <td colspan=1 style='text-align:left;font-size:13px;font-weight:bold'>Reg.#:</td>
            <td colspan=1 style='text-align:left;font-size:13px'>KM/7503/06</td>
            <td colspan=1 style='text-align:left;font-size:13px;font-weight:bold'>DOB:</td>
           {% set dob = frappe.db.get_value("Employee", {"name": doc.employee}, "date_of_birth") %}
            <td colspan=1 style='text-align:left;font-size:13px;border-right:2px solid black;'>{{frappe.utils.format_date(dob) or ''}}</td>
        </tr>
        <tr>
            <td colspan=1 style="text-align:left;border-left:2px solid black;font-size:13px;font-weight:bold">Name:</td>
            <td colspan=1 style='text-align:left;font-size:'>{{doc.employee_name}}</td>
            <td colspan=1 style='text-align:left;font-size:;font-weight:bold'>Days Worked:</td>
            <td colspan=1 style='text-align:left;font-size:13px'>{{doc.payment_days or ''}}</td>
            <td colspan=1 style='text-align:left;font-size:13px;font-weight:bold'>UAN #:</td>
            {% set pf_no = frappe.db.get_value("Employee", {"name": doc.employee}, "uan_no") %}
            <td colspan=1 style='text-align:left;font-size:13px'>{{pf_no or ''}}</td>
            <td colspan=1 style='text-align:left;font-size:13px;font-weight:bold'>DOJ:</td>
           {% set doj = frappe.db.get_value("Employee", {"name": doc.employee}, "date_of_joining") %}
            <td colspan=1 style='text-align:left;font-size:13px;border-right:2px solid black;'>{{frappe.utils.format_date(doj) or ''}}</td>
        </tr>
        <tr>
            <td colspan=1 style="text-align:left;border-left:2px solid black;font-size:13px;font-weight:bold;width:4%">Designation:</td>
            <td colspan=1 style='text-align:left;font-size:13px'>{{doc.designation or ''}}</td>
            <td colspan=1 style='text-align:left;font-size:13px;font-weight:bold'>Weekly Off:</td>
            <td colspan=1 style='text-align:left;font-size:13px'>{{doc.custom_weekly_off or ''}}</td>
            <td colspan=1 style='text-align:left;font-size:13px;font-weight:bold'>ESI #:</td>
            {% set esi_no = frappe.db.get_value("Employee", {"name": doc.employee}, "esi_no") %}
            <td colspan=1 style='text-align:left;font-size:13px'>{{esi_no or ''}}</td>
            <td colspan=1 style='text-align:left;font-size:13px;font-weight:bold'>A/c #:</td>
            <td colspan=1 style='text-align:left;font-size:13px;border-right:2px solid black;'>{{doc.bank_account_no or ''}}</td>
        </tr>
        <tr>
            <td colspan=2 style="border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;text-align:center"></td>
            <td colspan=1 style="border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;text-align:center">ACTUAL</td>
            <td colspan=1 style="border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;text-align:center">EARNINGS</td>
            <td colspan=3 style="border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;text-align:center">DEDUCTIONS</td>
            <td colspan=1 style="border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;text-align:center">AMOUNT/RS</td>
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Basic Wages</td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;;text-align:right;">{{ "{:,.2f}".format(doc.custom_basic) or ''}}</td>
            {% set basic = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Basic"}, "amount") %}
            {% if basic is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(basic) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Provident Fund</td>
            {% set pf = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Provident Fund"}, "amount") %}
            {% if pf is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(pf) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Dearness Allowance</td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;;text-align:right;">{{"{:,.2f}".format(doc.custom_dearness_allowance) or ''}}</td>
            {% set da = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Dearness Allowance"}, "amount") %}
            {% if da is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(da) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">PF (Voluntry Contribution)</td>
            {% set pfvc = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "PF (Voluntry Contribution)"}, "amount") %}
            {% if pfvc is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(pfvc) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">HRA</td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;;text-align:right;">{{"{:,.2f}".format(doc.custom_house_rent_allowance) or ''}}</td>
            {% set hra = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "House Rent Allowance"}, "amount") %}
            {% if hra is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(hra) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Employee State Insurance</td>
            {% set esi = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Employee State Insurance"}, "amount") %}
            {% if esi is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(esi) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Washing Allowance</td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;;text-align:right;">{{"{:,.2f}".format(doc.custom_washing_allowance) or ''}}</td>
            {% set wa = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Washing Allowance"}, "amount") %}
            {% if wa is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(wa) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Company Advance</td>
            {% set adv = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Advance"}, "amount") %}
            {% if adv is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(adv) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Conveyance Allowance</td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;;text-align:right;">{{"{:,.2f}".format(doc.custom_conveyance_allowance) or ''}}</td>
            {% set ca = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Conveyance Allowance"}, "amount") %}
            {% if ca is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(ca) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Bank Loan</td>
            {% set bl = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Bank Loan"}, "amount") %}
            {% if bl is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(bl) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Educational Allowance</td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;;text-align:right;">{{"{:,.2f}".format(doc.custom_education_allowance) or ''}}</td>
            {% set ea = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Education Allowance"}, "amount") %}
            {% if ea is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(ea) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Canteen</td>
            {% set ct = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Canteen"}, "amount") %}
            {% if ct is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(ct) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Performance Allowance</td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;;text-align:right;">{{"{:,.2f}".format(doc.custom_performance_allowance) or ''}}</td>
            {% set pa = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Performance Allowance"}, "amount") %}
            {% if pa is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(pa) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Other Debit</td>
            {% set od = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Other Deduction"}, "amount") %}
            {% if od is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(od) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Extra Wages & Incentives</td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% set ewi = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Overtime Amount"}, "amount") %}
            {% if ewi is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(ewi) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Professional Tax</td>
            {% set pt = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Professional Tax"}, "amount") %}
            {% if pt is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(pt) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Heat Allowance</td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% set ha = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Heat Allowance"}, "amount") %}
            {% if ha is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(ha) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Labour Welfare Fund</td>
            {% set lwf = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Labour Welfare Fund"}, "amount") %}
            {% if lwf is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(lwf) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Attendance Incentive</td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;;text-align:right;"></td>
            {% set ai = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Attendance Incentive"}, "amount") %}
            {% if ai is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(ai) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">QR Debit</td>
            {% set qrb = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "QR Debit"}, "amount") %}
            {% if qrb is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(qrb) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Other Allowance</td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% set oa = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Other Allowance"}, "amount") %}
            {% if oa is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(oa) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Group Insurance</td>
            {% set gi = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Group Insurance"}, "amount") %}
            {% if gi is none %}
