            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Allowances</td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% set aw = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Special Allowance"}, "amount") %}
