css:
.print-format{
    margin-top:35%;
    line-height:14px;
    margin-left:5mm;
    margin-right:5mm;
    font-family: "Times New Roman", Times, serif;
}

html:
{% if doc.docstatus == 0 %}
<p style="text-align: right;">DRAFT</p>
{% endif %}
<div>
    <table width=100%>
        <tr>
            <td colspan=4 style="font-size:15px;font-weight:bold;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">WONJIN AUTOPARTS INDIA PVT LTD - MM NAGAR.</td>
            <td colspan=3 style="font-size:15px;font-weight:bold;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">PAYSLIP FOR THE MONTH OF </td>
            <td colspan=1 style="font-size:15px;text-align:center;font-weight:bold;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;">{{frappe.utils.format_date(doc.start_date ,'MMM-yy')}}</td>
        </tr>
        <tr>
            <td colspan=8 style="border-left:2px solid black;border-right:2px solid black;"></td>
        </tr>
        <tr>
            <td colspan=1 style="border-left:2px solid black;font-size:13px;font-weight:bold">E Code:</td>
            <td colspan=1 style='font-size:13px;width:15%'>{{doc.employee}}</td>
            <td colspan=1 style='font-size:13px;font-weight:bold'>Department:</td>
            <td colspan=1 style='font-size:13px'>{{doc.department or ''}}</td>
            <td colspan=1 style='font-size:13px;font-weight:bold'>Reg.#:</td>
            <td colspan=1 style='font-size:13px'>KM/7503/06</td>
            <td colspan=1 style='font-size:13px;font-weight:bold'>DOB:</td>
           {% set dob = frappe.db.get_value("Employee", {"name": doc.employee}, "date_of_birth") %}
            <td colspan=1 style='font-size:13px;border-right:2px solid black;'>{{frappe.utils.format_date(dob) or ''}}</td>
        </tr>
        <tr>
            <td colspan=1 style="border-left:2px solid black;font-size:13px;font-weight:bold">Name:</td>
            <td colspan=1 style='font-size:13px'>{{doc.employee_name}}</td>
            <td colspan=1 style='font-size:13px;font-weight:bold'>Days Worked:</td>
            <td colspan=1 style='font-size:13px'>{{doc.payment_days or ''}}</td>
            <td colspan=1 style='font-size:13px;font-weight:bold'>UAN #:</td>
            {% set pf_no = frappe.db.get_value("Employee", {"name": doc.employee}, "uan_no") %}
            <td colspan=1 style='font-size:13px'>{{pf_no or ''}}</td>
            <td colspan=1 style='font-size:13px;font-weight:bold'>DOJ:</td>
           {% set doj = frappe.db.get_value("Employee", {"name": doc.employee}, "date_of_joining") %}
            <td colspan=1 style='font-size:13px;border-right:2px solid black;'>{{frappe.utils.format_date(doj) or ''}}</td>
        </tr>
        <tr>
            <td colspan=1 style="border-left:2px solid black;font-size:13px;font-weight:bold">Designation:</td>
            <td colspan=1 style='font-size:13px'>{{doc.designation or ''}}</td>
            <td colspan=1 style='font-size:13px;font-weight:bold'>Weekly Off:</td>
            <td colspan=1 style='font-size:13px'>{{doc.custom_weekly_off or ''}}</td>
            <td colspan=1 style='font-size:13px;font-weight:bold'>ESI #:</td>
            {% set esi_no = frappe.db.get_value("Employee", {"name": doc.employee}, "esi_no") %}
            <td colspan=1 style='font-size:13px'>{{esi_no or ''}}</td>
            <td colspan=1 style='font-size:13px;font-weight:bold'>A/c #:</td>
            <td colspan=1 style='font-size:13px;border-right:2px solid black;'>{{doc.bank_account_no or ''}}</td>
        </tr>
        <tr>
            <td colspan=2 style="border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;text-align:center">PARTICULARS</td>
            <td colspan=1 style="border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;text-align:center">FIXED</td>
            <td colspan=1 style="border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;text-align:center">EARNINGS</td>
            <td colspan=3 style="border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;text-align:center">DEDUCTIONS</td>
            <td colspan=1 style="border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;text-align:center">AMOUNT/RS</td>
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Basic & DA</td>
            <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{"{:,.2f}".format(doc.custom_basic_da) or ''}}</td>
            {% set basic = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Basic & DA"}, "amount") %}
            {% if basic is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(basic) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Provident Fund</td>
            {% set pf = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Provident Fund"}, "amount") %}
            {% if pf is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(pf) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">HRA</td>
            <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{"{:,.2f}".format(doc.custom_house_rent_allowance) or ''}}</td>
            {% set hra = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "House Rent Allowance"}, "amount") %}
            {% if hra is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(hra) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">VPF (Voluntry Contribution)</td>
            {% set pfvc = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Voluntry Provident Fund"}, "amount" or '')%}
            {% if pfvc is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(pfvc) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">LTA</td>
            <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{"{:,.2f}".format(doc.custom_leave_travel_allowance) or ''}}</td>
            {% set lta = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Leave Travel Allowance"}, "amount") %}
            {% if lta is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(lta) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Employee State Insurance</td>
            {% set esi = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Employee State Insurance"}, "amount") %}
            {% if esi is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(esi) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Medical & Conveyance Allowance</td>
            <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{"{:,.2f}".format(doc.custom_medical_conveyance_allowance) or ''}}</td>
            {% set mca = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Medical & Conveyance Allowance"}, "amount") %}
            {% if mca is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(mca) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Company Advance</td>
            {% set adv = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Advance"}, "amount") %}
            {% if adv is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(adv) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Special Pay</td>
            <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{"{:,.2f}".format(doc.custom_special_allowance) or ''}}</td>
            {% set sp = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Special Pay"}, "amount") %}
            {% if sp is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(sp) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Bank Loan</td>
            {% set bl = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Bank Loan"}, "amount") %}
            {% if bl is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(bl) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Performance Allowance</td>
            <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{"{:,.2f}".format(doc.custom_performance_allowance) or ''}}</td>
            {% set pa = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Performance Allowance"}, "amount") %}
            {% if pa is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(pa) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Canteen</td>
            {% set ct = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Canteen"}, "amount") %}
            {% if ct is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(ct) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Night Shift Allowance</td>
            <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% set nsa = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Night Shift Allowance"}, "amount")%}
            {% if nsa is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(nsa) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Group Insurance</td>
            {% set gi = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Group Insurance"}, "amount") %}
            {% if gi is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(gi) }}</td>
            {% endif %}</tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Other Allowance</td>
            <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% set oa = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Other Allowance"}, "amount") %}
            {% if oa is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(oa) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Contribution</td>
            {% set ct = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Contribution"}, "amount") %}
            {% if ct is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(ct) }}</td>
            {% endif %}</tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Stiching Charge</td>
            <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% set sc = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Stiching Charge"}, "amount") %}
            {% if sc is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(sc) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Debit</td>
            {% set dt = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Debit"}, "amount") %}
            {% if dt is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(dt) }}</td>
            {% endif %}</tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Birthday Gift</td>
            <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% set birthday = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Birthday Allowance"}, "amount") %}
            {% if birthday is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(birthday) }}</td>
            {% endif %}
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Professional Tax</td>
            {% set pt = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Professional Tax"}, "amount") %}
            {% if pt is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(pt) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Travel Allowance</td>
            <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% set ta = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Travel Allowances"}, "amount") %}
            {% if ta is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(ta) }}</td>
            {% endif %}<td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Labour Welfare Fund</td>
            {% set lwf = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Labour Welfare Fund"}, "amount") %}
            {% if lwf is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(lwf) }}</td>
            {% endif %}
        </tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Marrigae Contribution</td>
            {% set mrc = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Marriage Contribution"}, "amount") %}
            {% if mrc is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(mrc) }}</td>
            {% endif %}</tr>
        <tr>
            <td colspan=2 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            <td colspan=1 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            <td colspan=3 style="border-left:2px solid black;border-right:2px solid black;font-size:13px;">Late Penalty</td>
            {% set mrc = frappe.db.get_value("Salary Detail", {"parent": doc.name, "salary_component": "Late Penalty"}, "amount") %}
            {% if mrc is none %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;"></td>
            {% else %}
                <td colspan=1 style="text-align:right;border-left:2px solid black;border-right:2px solid black;font-size:13px;">{{ "{:,.2f}".format(mrc) }}</td>
            {% endif %}</tr>
        <tr>
            <td colspan=2 style="border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;text-align:center">TOTAL</td>
            <td colspan=1 style="text-align:right;text-align:right;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold">{{"{:,.2f}".format(doc.custom_total_fixed_amount) or ''}}</td>
            <td colspan=1 style="text-align:right;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;">{{"{:,.2f}".format(doc.gross_pay) or ''}}</td>
            <td colspan=3 style="border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;;text-align:center">TOTAL</td>
            <td colspan=1 style="text-align:right;border-bottom:2px solid black;border-left:2px solid black;border-top:2px solid black;border-right:2px solid black;font-size:13px;font-weight:bold;">{{"{:,.2f}".format(doc.total_deduction) or ''}}</td>
